var assert = require("assert");
var should = require('should');


var isSDK = require('../lib/isSDK.js');
var config = require("./config.js");

isSDK.initialize(config.infusionsoft.APIName, config.infusionsoft.APIKey);


describe("Array", function(){
    describe("#appEcho()", function(){
        it("should return 'Hello World'", function(done){
            isSDK.appEcho("Hello World", function(error, value){
                if(error) throw error;
                value.should.eql("Hello World");
                done();
            });
        })
    });
});


